mapper.update:
UPDATE user_configs u
SET value = #{value}, user_id = #{userId}
WHERE u.id = #{id} AND user_id = #{userId};

UPDATE user_configs
SET value = #{value}
WHERE id = #{id} AND user_id = #{userId} AND is_deleted = false

mapper.select:
SELECT COUNT(*) > 0
FROM user_configs
WHERE value = #{value}
